     <!-- /m_editable -->
     <div class="colelem shared_content" id="u78765" data-content-guid="u78765_content"><!-- simple frame --></div>
     <!-- m_editable region-id="editable-static-tag-U78517-BP_infinity" template="the-hotel.html" data-type="html" data-ice-options="disableImageResize,link,txtStyleTarget" -->
     <div class="text-body-padding-top-5px ASM---p---body-text clearfix colelem shared_content" id="u78517-4" data-muse-uid="U78517" data-muse-type="txt_frame" data-IBE-flags="txtStyleSrc" data-content-guid="u78517-4_content"><!-- content -->
      <p id="u78517-2">True to Montréal's reputation as a gourmet destination, The Fairmont Queen Elizabeth offers a wide selection of dining options to please both local and international foodies - from early morning until late at night.</p>
     </div>
     <!-- /m_editable -->
     <div class="clearfix colelem shared_content" id="ppu78795-4" data-content-guid="ppu78795-4_content"><!-- group -->
