      <div class="ASM-h3-Subheading clearfix grpelem shared_content" id="u78855-4" data-muse-uid="U78855" data-muse-type="txt_frame" data-IBE-flags="txtStyleSrc" data-content-guid="u78855-4_content"><!-- content -->
       <h1 id="u78855-2">KREMA LOUNGE &amp; COFFEE BAR</h1>
      </div>
      <!-- /m_editable -->
      <div class="museBGSize grpelem shared_content" id="u82892" data-content-guid="u82892_content"><!-- simple frame --></div>
