       </div>
       <!-- /m_editable -->
       <div class="colelem shared_content" id="u95503" data-content-guid="u95503_content"><!-- simple frame --></div>
       <!-- m_editable region-id="editable-static-tag-U78922-BP_infinity" template="the-hotel.html" data-type="html" data-ice-options="disableImageResize,link,txtStyleTarget" -->
