     <!-- /m_editable -->
     <div class="colelem shared_content" id="u400568" data-content-guid="u400568_content"><!-- simple frame --></div>
     <!-- m_editable region-id="editable-static-tag-U400589-BP_infinity" template="the-hotel.html" data-type="html" data-ice-options="disableImageResize,link,txtStyleTarget" -->
     <div class="text-body-padding-top-5px ASM---p---body-text clearfix colelem shared_content" id="u400589-4" data-muse-uid="U400589" data-muse-type="txt_frame" data-IBE-flags="txtStyleSrc" data-content-guid="u400589-4_content"><!-- content -->
      <p id="u400589-2">Relax &amp; unwind, or keep up with your workout regime, our Spa and Wellness Center feature state-of-the-art facilities, along with friendly and knowledgeable staff.</p>
     </div>
     <!-- /m_editable -->
     <div class="clearfix colelem shared_content" id="ppu79923-4" data-content-guid="ppu79923-4_content"><!-- group -->
