       <!-- /m_editable -->
       <div class="colelem shared_content" id="u400661" data-content-guid="u400661_content"><!-- simple frame --></div>
       <!-- m_editable region-id="editable-static-tag-U400640-BP_infinity" template="the-hotel.html" data-type="html" data-ice-options="disableImageResize,link,txtStyleTarget" -->
